public class AtmInterface {    public static void main(String[] args) {        Scanner sc=new Scanner(System.in);        System.out.println("Enter the Initial balance");        int bal=sc.nextInt();        int amt=0;        String z="";        account ac=new account((int)Math.random()*1000000000,bal);        while(true){        System.out.println("Enter 1.withdraw 2.Deposit 3.checkBalance");        int choice=sc.nextInt();        switch(choice){            case 1:                System.out.println("Enter the amount to Withdraw");                amt=sc.nextInt();                ac.withdraw(amt);                break;            case 2:                System.out.println("Enter the amount to Deposit");                amt=sc.nextInt();                ac.Deposit(amt);                break;            case 3:                int x=ac.getBalance();                System.out.println("The current Balance of the User is "+ x);                break;            default:                System.out.println("Enter a vaild choice!");        }        System.out.println("To CONTINUE Banking Press any Key  To EXIT press \"N\" ");        z=sc.next();        if(z.equals("N")) break;        }            }    }